```{.r .cell-code}
game_count_df <- Teams |>
  select(yearID, G) |>
  group_by(yearID, G) |>
  mutate(obs_count = n()) |>
  ungroup() |>
  distinct() |>

  # compute mode by year
  group_by(yearID) |>
  slice_max(order_by = obs_count, n = 1) |>
